mapper.update:
update dish
name = #{name}, image = #{image}, description = #{description}, category_id = #{categoryId}, price = #{price}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select d.*,c.name categoryName from dish d left join category c on d.category_id = c.id

    d.name like concat('%',#{name},'%')
    and d.category_id = #{categoryId}
    and d.status = #{status}

order by create_time desc,name asc
